mapper.update:
UPDATE post
   SET read_cnt = read_cnt + 1
 WHERE board_id = #{boardId}
   AND post_id = #{postId}

mapper.insert:
INSERT
  INTO post
     (
       board_id
     , title
     , content
     , read_cnt
     , comment_cnt
     , use_yn
     , created_by
     , updated_by
     )
VALUES
     (
       #{postDto.boardId}
     , #{postDto.title}
     , #{postDto.content}
     , 0
     , 0
     , 'Y'
     , #{memberId}
     , #{memberId}
     )

mapper.update:
UPDATE post
   SET title = #{postModifyDto.title}
     , content = #{postModifyDto.content}
     , updated_at = now()
     , updated_by = #{memberId}
 WHERE board_id = #{postModifyDto.boardId}
   AND post_id = #{postModifyDto.postId}

mapper.delete:
DELETE FROM post
 WHERE board_id = #{boardId}
   AND post_id = #{postId}
